import React, { useEffect, useState } from 'react';
import './DeleteScheme.css'; // Ensure the correct path to your CSS file

const DeleteScheme = () => {
  const [schemes, setSchemes] = useState([]);

  useEffect(() => {
    const fetchSchemes = async () => {
      try {
        const response = await fetch('http://yourapiurl.com/schemes'); // Replace with your actual URL
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        console.log('Fetched schemes:', data); // Log the fetched data
        setSchemes(data);
      } catch (error) {
        console.error('Failed to fetch schemes:', error);
      }
    };

    fetchSchemes();
  }, []);

  const handleDelete = async (index) => {
    if (window.confirm("Are you sure you want to delete this scheme?")) {
      try {
        const schemeToDelete = schemes[index];
        // Assuming each scheme has an _id field for deletion
        await fetch(`http://yourapiurl.com/schemes/${schemeToDelete._id.$oid}`, {
          method: 'DELETE',
        });
        const updatedSchemes = schemes.filter((_, i) => i !== index);
        setSchemes(updatedSchemes);
      } catch (error) {
        console.error('Failed to delete scheme:', error);
      }
    }
  };

  return (
    <div className="schemes-container">
      {schemes.length > 0 ? (
        schemes.map((scheme, index) => (
          <div key={scheme._id.$oid} className="scheme-card"> {/* Using scheme._id for unique key */}
            <h3>{scheme.name}</h3>
            <p>{scheme.description}</p>
            <h4>Target Audience: {scheme.target_audience}</h4> {/* Accessing target audience correctly */}
            <h4>Investment Plan:</h4>
            <ul>
              <li>Monthly Contribution: ₹{scheme.investment_plan.monthly_contribution}</li>
              <li>Chit Period: {scheme.investment_plan.chit_period} months</li>
              <li>Total Fund Value:</li>
              <ul>
                {scheme.investment_plan.total_fund_value.map((value, i) => (
                  <li key={value._id.$oid}>
                    {value.duration} months: ₹{value.value}
                  </li>
                ))}
              </ul>
            </ul>
            <h4>Benefits:</h4>
            <ul>
              {scheme.benefits.map((benefit, i) => (
                <li key={i}>{benefit}</li>
              ))}
            </ul>
            <button onClick={() => handleDelete(index)} className="delete-btn">Delete</button>
          </div>
        ))
      ) : (
        <p>No schemes available.</p> // Fallback message if no schemes are fetched
      )}
    </div>
  );
};

export default DeleteScheme;
