import React, { useEffect, useState } from 'react';
import './DeleteScheme.css'; // Make sure to include the correct path to your CSS file
import axios from 'axios';

const Schemes = () => {
  const [schemes, setSchemes] = useState([]);

  // Fetch schemes from the server
  useEffect(() => {
    const fetchSchemes = async () => {
      try {
        const response = await axios.get('http://localhost:5001/admin/schemes', {
          withCredentials: true, // Important for session cookies
        });
        setSchemes(response.data);
      } catch (error) {
        console.error('Error fetching schemes:', error);
      }
    };

    fetchSchemes();
  }, []);

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5001/admin/schemes/${id}`, {
        withCredentials: true, // Important for session cookies
      });
      // Filter out the deleted scheme from the state
      setSchemes(schemes.filter(scheme => scheme._id !== id));
    } catch (error) {
      console.error('Error deleting scheme:', error);
    }
  };

  return (
    <div className="schemes-container">
      {schemes.map((scheme) => (
        <div key={scheme._id} className="scheme-card">
          <h3>{scheme.name}</h3>
          <p>{scheme.description}</p>
          <h4>Target Audience: {scheme.target_audience}</h4>
          <h4>Investment Plan:</h4>
          <ul>
            <li>Monthly Contribution: ₹{scheme.investment_plan.monthly_contribution}</li>
            <li>Chit Period: {scheme.investment_plan.chit_period} months</li>
            <h5>Total Fund Value:</h5>
            {scheme.investment_plan.total_fund_value.map((value, i) => (
              <li key={i}>{value.duration} months: ₹{value.value}</li>
            ))}
          </ul>
          <h4>Benefits:</h4>
          <ul>
            {scheme.benefits.map((benefit, i) => (
              <li key={i}>{benefit}</li>
            ))}
          </ul>
          <button onClick={() => handleDelete(scheme._id)} className="delete-btn">Delete</button>
        </div>
      ))}
    </div>
  );
};

export default Schemes;
