import React, { useEffect, useState } from 'react';

const DeleteScheme = () => {
  const [schemes, setSchemes] = useState([]);

  const fetchSchemes = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/admin-schemes');
      const data = await response.json();
      console.log('Fetched schemes:', data); // Log the response data
      setSchemes(data);
    } catch (error) {
      console.error('Error fetching schemes:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      const response = await fetch(`http://localhost:5000/api/admin-schemes/${id}`, {
        method: 'DELETE'
      });
      if (response.ok) {
        setSchemes(schemes.filter(scheme => scheme._id !== id));
      }
    } catch (error) {
      console.error('Error deleting scheme:', error);
    }
  };

  useEffect(() => {
    fetchSchemes();
  }, []);

  return (
    <div>
      <h2>Manage Schemes</h2>
      {schemes.length === 0 ? (
        <p>No schemes available</p>
      ) : (
        schemes.map(scheme => (
          <div key={scheme._id}>
            <h3>{scheme.name}</h3>
            <p>{scheme.description}</p>
            <button onClick={() => handleDelete(scheme._id)}>Delete</button>
          </div>
        ))
      )}
    </div>
  );
};

export default DeleteScheme;
