import React, { useState, useEffect } from 'react';
import './DeleteScheme.css'; // Ensure the correct path to your CSS file
import axios from 'axios'; // Make sure axios is installed

const Schemes = () => {
  const [schemes, setSchemes] = useState([]);

  // Fetch schemes from the database
  useEffect(() => {
    const fetchSchemes = async () => {
      try {
        const response = await axios.get('/api/schemes'); // Adjust the URL if needed
        setSchemes(response.data); // Assuming response.data is an array of schemes
      } catch (error) {
        console.error('Error fetching schemes:', error);
      }
    };

    fetchSchemes();
  }, []); // Empty dependency array to run only on mount

  const handleDelete = async (index) => {
    const schemeToDelete = schemes[index];
    try {
      await axios.delete(`/api/schemes/${schemeToDelete.id}`); // Assuming you have a delete endpoint
      const updatedSchemes = schemes.filter((_, i) => i !== index);
      setSchemes(updatedSchemes);
    } catch (error) {
      console.error('Error deleting scheme:', error);
    }
  };

  return (
    <div className="schemes-container">
      {schemes.length === 0 ? (
        <p>No schemes available.</p>
      ) : (
        schemes.map((scheme, index) => (
          <div key={index} className="scheme-card">
            <h3>{scheme.name}</h3>
            <p>{scheme.description}</p>
            <h4>Target Audience: {scheme.targetAudience}</h4>
            <h4>Investment Plan:</h4>
            <ul>
              {scheme.investmentPlan.map((plan, i) => (
                <li key={i}>{plan}</li>
              ))}
            </ul>
            <h4>Benefits:</h4>
            <ul>
              {scheme.benefits.map((benefit, i) => (
                <li key={i}>{benefit}</li>
              ))}
            </ul>
            <button onClick={() => handleDelete(index)} className="delete-btn">Delete</button>
          </div>
        ))
      )}
    </div>
  );
};

export default Schemes;
