import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Make sure you have axios installed
import './DeleteScheme.css'; // Ensure the correct path to your CSS file

const DeleteScheme = () => {
  const [schemes, setSchemes] = useState([]);

  // Function to fetch schemes from the database
  const fetchSchemes = async () => {
    try {
      const response = await axios.get('YOUR_API_ENDPOINT_HERE'); // Replace with your actual endpoint
      setSchemes(response.data); // Adjust according to your response structure
    } catch (error) {
      console.error("Error fetching schemes:", error);
    }
  };

  useEffect(() => {
    fetchSchemes(); // Call the fetch function on component mount
  }, []);

  const handleDelete = async (index) => {
    if (window.confirm("Are you sure you want to delete this scheme?")) {
      try {
        await axios.delete(`YOUR_API_ENDPOINT_HERE/${schemes[index]._id}`); // Delete the scheme from the backend
        const updatedSchemes = schemes.filter((_, i) => i !== index);
        setSchemes(updatedSchemes); // Update local state
      } catch (error) {
        console.error("Error deleting scheme:", error);
      }
    }
  };

  return (
    <div className="schemes-container">
      {schemes.map((scheme, index) => (
        <div key={scheme._id} className="scheme-card"> {/* Use scheme._id as the key */}
          <h3>{scheme.name}</h3>
          <p>{scheme.description}</p>
          <h4>Target Audience: {scheme.target_audience}</h4>
          <h4>Investment Plan:</h4>
          <ul>
            <li>Monthly Contribution: ₹{scheme.investment_plan.monthly_contribution}</li>
            <li>Chit Period: {scheme.investment_plan.chit_period} months</li>
            <li>Total Fund Value:</li>
            <ul>
              {scheme.investment_plan.total_fund_value.map((value, i) => (
                <li key={i}>Duration: {value.duration} months, Value: ₹{value.value}</li>
              ))}
            </ul>
          </ul>
          <h4>Benefits:</h4>
          <ul>
            {scheme.benefits.map((benefit, i) => (
              <li key={i}>{benefit}</li>
            ))}
          </ul>
          <button onClick={() => handleDelete(index)} className="delete-btn">Delete</button>
        </div>
      ))}
    </div>
  );
};

export default DeleteScheme;
