import React, { useEffect, useState } from 'react';
import './DeleteScheme.css'; // Make sure to include the correct path to your CSS file

const Schemes = () => {
  const [schemes, setSchemes] = useState([]);

  // Fetch schemes from the backend when the component mounts
  useEffect(() => {
    const fetchSchemes = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/admin-schemes');
        if (!response.ok) {
          throw new Error('Failed to fetch schemes');
        }
        const data = await response.json();
        setSchemes(data);
      } catch (error) {
        console.error('Error fetching schemes:', error);
      }
    };

    fetchSchemes();
  }, []);

  // Function to handle deletion of a scheme
  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this scheme?')) {
      try {
        const response = await fetch(`http://localhost:5000/api/admin-schemes/${id}`, {
          method: 'DELETE',
        });
        if (!response.ok) {
          throw new Error('Failed to delete scheme');
        }
        // Update state to remove the deleted scheme
        setSchemes((prevSchemes) => prevSchemes.filter((scheme) => scheme._id !== id));
      } catch (error) {
        console.error('Error deleting scheme:', error);
      }
    }
  };

  return (
    <div className="schemes-container">
      {schemes.length === 0 ? (
        <p>No schemes available</p>
      ) : (
        schemes.map((scheme) => (
          <div key={scheme._id} className="scheme-card">
            <h3>{scheme.name}</h3>
            <p>{scheme.description}</p>
            <h4>Target Audience: {scheme.targetAudience}</h4>
            <h4>Investment Plan:</h4>
            <ul>
              {scheme.investmentPlan && scheme.investmentPlan.total_fund_value.map((item, i) => (
                <li key={i}>
                  Monthly Contribution: {scheme.investmentPlan.monthly_contribution} <br />
                  Chit Period: {scheme.investmentPlan.chit_period} months <br />
                  {item.duration} months: â‚¹{item.value}
                </li>
              ))}
            </ul>
            <h4>Benefits:</h4>
            <ul>
              {scheme.benefits.map((benefit, i) => (
                <li key={i}>{benefit}</li>
              ))}
            </ul>
            <button onClick={() => handleDelete(scheme._id)} className="delete-btn">Delete</button>
          </div>
        ))
      )}
    </div>
  );
};

export default Schemes;
