import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './DeleteScheme.css';

const Schemes = () => {
  const [schemes, setSchemes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch schemes from the backend API
    const fetchSchemes = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/schemes'); // Assuming localhost and correct port
        setSchemes(response.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch schemes');
        setLoading(false);
      }
    };

    fetchSchemes();
  }, []);

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/schemes/${id}`);
      setSchemes(schemes.filter(scheme => scheme._id !== id)); // Filter out deleted scheme from state
    } catch (err) {
      console.error('Failed to delete scheme', err);
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="schemes-container">
      {schemes.map((scheme) => (
        <div key={scheme._id} className="scheme-card">
          <h3>{scheme.name}</h3>
          <p>{scheme.description}</p>
          <h4>Target Audience: {scheme.targetAudience}</h4>
          <h4>Investment Plan:</h4>
          <ul>
            {scheme.investmentPlan.map((plan, i) => (
              <li key={i}>{plan}</li>
            ))}
          </ul>
          <h4>Benefits:</h4>
          <ul>
            {scheme.benefits.map((benefit, i) => (
              <li key={i}>{benefit}</li>
            ))}
          </ul>
          <button onClick={() => handleDelete(scheme._id)} className="delete-btn">Delete</button>
        </div>
      ))}
    </div>
  );
};

export default Schemes;
