import React, { useEffect, useState } from 'react';
import './DeleteScheme.css'; // Ensure this path is correct

const DeleteScheme = () => {
  const [schemes, setSchemes] = useState([]);

  useEffect(() => {
    const fetchSchemes = async () => {
      try {
        const response = await fetch('/api/schemes'); // Fetch schemes from your backend API
        const data = await response.json();
        setSchemes(data);
      } catch (error) {
        console.error('Error fetching schemes:', error);
      }
    };

    fetchSchemes();
  }, []);

  const handleDelete = async (id) => {
    try {
      await fetch(`/api/schemes/${id}`, { method: 'DELETE' }); // DELETE request to your backend API
      setSchemes(schemes.filter(scheme => scheme._id !== id)); // Update local state after deletion
      alert('Scheme deleted successfully!');
    } catch (error) {
      console.error('Error deleting scheme:', error);
      alert('Error deleting scheme. Please try again.');
    }
  };

  return (
    <div className="schemes-container">
      {schemes.map((scheme, index) => (
        <div key={scheme._id} className="scheme-card">
          <h3>{scheme.name}</h3>
          <p>{scheme.description}</p>
          <h4>Target Audience: {scheme.targetAudience}</h4>
          <h4>Investment Plan:</h4>
          <ul>
            {scheme.investmentPlan.map((plan, i) => (
              <li key={i}>{plan}</li>
            ))}
          </ul>
          <h4>Benefits:</h4>
          <ul>
            {scheme.benefits.map((benefit, i) => (
              <li key={i}>{benefit}</li>
            ))}
          </ul>
          <button onClick={() => handleDelete(scheme._id)} className="delete-btn">Delete</button>
        </div>
      ))}
    </div>
  );
};

export default DeleteScheme;
