import React, { useEffect, useState } from 'react';

const SchemesDisplay = () => {
  const [schemes, setSchemes] = useState([]);

  const fetchSchemes = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/admin-schemes');
      const data = await response.json();
      console.log('Fetched schemes:', data); // Log the response data
      setSchemes(data);
    } catch (error) {
      console.error('Error fetching schemes:', error);
    }
  };

  useEffect(() => {
    fetchSchemes();
  }, []);

  return (
    <div>
      <h2>Available Schemes</h2>
      {schemes.length === 0 ? (
        <p>No schemes available</p>
      ) : (
        <div>
          {schemes.map(scheme => (
            <div key={scheme._id} style={{ border: '1px solid #ccc', margin: '10px', padding: '10px' }}>
              <h3>{scheme.name} <span style={{ fontSize: '1.2em' }}>{scheme.icon}</span></h3>
              <p><strong>Description:</strong> {scheme.description}</p>
              <p><strong>Target Audience:</strong> {scheme.target_audience}</p>
              <p><strong>Monthly Contribution:</strong> {scheme.investment_plan.monthly_contribution}</p>
              <p><strong>Chit Period:</strong> {scheme.investment_plan.chit_period} months</p>
              <p><strong>Total Fund Value:</strong></p>
              <ul>
                {scheme.investment_plan.total_fund_value.map((item, index) => (
                  <li key={index}>{item.duration} months: {item.value}</li>
                ))}
              </ul>
              <p><strong>Benefits:</strong></p>
              <ul>
                {scheme.benefits.map((benefit, index) => (
                  <li key={index}>{benefit}</li>
                ))}
              </ul>
              <p><strong>Number of Slots:</strong> {scheme.number_of_slots}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default SchemesDisplay;
