import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Alert, AlertDescription } from '@/components/ui/alert';

const DeleteScheme = () => {
  const [schemes, setSchemes] = useState([]);
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState('');

  // Fetch schemes on component mount
  useEffect(() => {
    fetchSchemes();
  }, []);

  const fetchSchemes = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/schemes');
      setSchemes(response.data);
      setError(null);
    } catch (err) {
      setError('Failed to fetch schemes. Please try again later.');
      console.error('Error fetching schemes:', err);
    }
  };

  const handleDelete = async (schemeId) => {
    try {
      await axios.delete(`http://localhost:5000/api/schemes/${schemeId}`);
      setSuccessMessage('Scheme deleted successfully!');
      // Refresh the schemes list
      fetchSchemes();
      
      // Clear success message after 3 seconds
      setTimeout(() => {
        setSuccessMessage('');
      }, 3000);
    } catch (err) {
      setError('Failed to delete scheme. Please try again.');
      console.error('Error deleting scheme:', err);
    }
  };

  return (
    <div className="schemes-container p-6">
      {error && (
        <Alert variant="destructive" className="mb-4">
          <AlertDescription>{error}</AlertDescription>
        </Alert>
      )}
      
      {successMessage && (
        <Alert className="mb-4 bg-green-100">
          <AlertDescription>{successMessage}</AlertDescription>
        </Alert>
      )}

      {schemes.map((scheme) => (
        <div key={scheme._id} className="scheme-card mb-6 p-6 border rounded-lg shadow-sm">
          <h3 className="text-xl font-bold mb-2">{scheme.name}</h3>
          <p className="mb-4 text-gray-600">{scheme.description}</p>
          
          <h4 className="font-semibold mb-2">Target Audience:</h4>
          <p className="mb-4">{scheme.target_audience}</p>
          
          <h4 className="font-semibold mb-2">Investment Plan:</h4>
          <ul className="list-disc pl-5 mb-4">
            <li>Monthly Contribution: ₹{scheme.investment_plan.monthly_contribution}</li>
            <li>Chit Period: {scheme.investment_plan.chit_period} months</li>
            {scheme.investment_plan.total_fund_value.map((fund, index) => (
              <li key={index}>
                {fund.duration}-month plan: ₹{fund.value}
              </li>
            ))}
          </ul>
          
          <h4 className="font-semibold mb-2">Benefits:</h4>
          <ul className="list-disc pl-5 mb-4">
            {scheme.benefits.map((benefit, index) => (
              <li key={index}>{benefit}</li>
            ))}
          </ul>
          
          <button
            onClick={() => handleDelete(scheme._id)}
            className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded transition-colors"
          >
            Delete
          </button>
        </div>
      ))}
    </div>
  );
};

export default DeleteScheme;
