import React, { useEffect, useState } from 'react';
import './DeleteScheme.css'; // Make sure to include the correct path to your CSS file
import axios from 'axios';

const DeleteScheme = () => {
  const [schemes, setSchemes] = useState([]);
  const [selectedSchemeId, setSelectedSchemeId] = useState('');

  useEffect(() => {
    const fetchSchemes = async () => {
      try {
        const response = await axios.get('/api/schemes'); // Endpoint to fetch all schemes
        setSchemes(response.data);
      } catch (error) {
        console.error('Error fetching schemes:', error);
      }
    };

    fetchSchemes();
  }, []);

  const handleDelete = async () => {
    try {
      await axios.delete(`/api/schemes/${selectedSchemeId}`); // Endpoint to delete a scheme
      setSchemes(schemes.filter(scheme => scheme._id !== selectedSchemeId)); // Update state after deletion
      setSelectedSchemeId(''); // Clear selection
      alert('Scheme deleted successfully!');
    } catch (error) {
      console.error('Error deleting scheme:', error);
      alert('Error deleting scheme. Please try again.');
    }
  };

  return (
    <div className="delete-scheme-container">
      <h2>Delete Scheme</h2>
      <div className="delete-scheme-form">
        <label htmlFor="scheme-select">Select Scheme to Delete:</label>
        <select
          id="scheme-select"
          value={selectedSchemeId}
          onChange={e => setSelectedSchemeId(e.target.value)}
        >
          <option value="">--Select a Scheme--</option>
          {schemes.map(scheme => (
            <option key={scheme._id} value={scheme._id}>
              {scheme.name}
            </option>
          ))}
        </select>
        <button onClick={handleDelete} disabled={!selectedSchemeId}>
          Delete Scheme
        </button>
      </div>
    </div>
  );
};

export default DeleteScheme;
