import React, { useEffect, useState } from 'react';
import './DeleteScheme.css'; // Ensure the correct path to your CSS file

const DeleteScheme = () => {
  const [schemes, setSchemes] = useState([]);

  // Fetch schemes from the database/API
  useEffect(() => {
    const fetchSchemes = async () => {
      try {
        const response = await fetch('http://yourapiurl.com/schemes'); // Replace with your API endpoint
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setSchemes(data); // Assuming the response is an array of schemes
      } catch (error) {
        console.error('Failed to fetch schemes:', error);
      }
    };

    fetchSchemes();
  }, []); // Empty dependency array means this runs once on mount

  const handleDelete = (index) => {
    if (window.confirm("Are you sure you want to delete this scheme?")) {
      const updatedSchemes = schemes.filter((_, i) => i !== index);
      setSchemes(updatedSchemes);
      // Optionally, you could also make a DELETE request to your API to remove the scheme from the database.
    }
  };

  return (
    <div className="schemes-container">
      {schemes.map((scheme, index) => (
        <div key={index} className="scheme-card">
          <h3>{scheme.name}</h3>
          <p>{scheme.description}</p>
          <h4>Target Audience: {scheme.targetAudience}</h4>
          <h4>Investment Plan:</h4>
          <ul>
            {scheme.investmentPlan.map((plan, i) => (
              <li key={i}>{plan}</li>
            ))}
          </ul>
          <h4>Benefits:</h4>
          <ul>
            {scheme.benefits.map((benefit, i) => (
              <li key={i}>{benefit}</li>
            ))}
          </ul>
          <button onClick={() => handleDelete(index)} className="delete-btn">Delete</button>
        </div>
      ))}
    </div>
  );
};

export default DeleteScheme;
