// DeleteSchemes.js
import React, { useEffect, useState } from 'react';

const SchemesDisplay = () => {
  const [schemes, setSchemes] = useState([]);

  // Fetch schemes from the backend
  const fetchSchemes = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/admin-schemes');
      const data = await response.json();
      console.log('Fetched schemes:', data);
      setSchemes(data);
    } catch (error) {
      console.error('Error fetching schemes:', error);
    }
  };

  // Function to delete a scheme
  const deleteScheme = async (id) => {
    if (window.confirm('Are you sure you want to delete this scheme?')) {
      try {
        const response = await fetch(`http://localhost:5000/api/admin-schemes/${id}`, {
          method: 'DELETE',
        });
        if (response.ok) {
          console.log('Scheme deleted:', id);
          // Update state to remove deleted scheme
          setSchemes(schemes.filter(scheme => scheme._id !== id));
        } else {
          const errorMessage = await response.json();
          console.error('Error deleting scheme:', errorMessage);
        }
      } catch (error) {
        console.error('Error deleting scheme:', error);
      }
    }
  };

  useEffect(() => {
    fetchSchemes(); // Fetch schemes when the component mounts
  }, []);

  return (
    <div>
      <h2>Available Schemes</h2>
      {schemes.length === 0 ? (
        <p>No schemes available</p>
      ) : (
        <div>
          {schemes.map(scheme => (
            <div key={scheme._id} style={{ border: '1px solid #ccc', margin: '10px', padding: '10px' }}>
              <h3>{scheme.name} <span style={{ fontSize: '1.2em' }}>{scheme.icon}</span></h3>
              <p><strong>Description:</strong> {scheme.description}</p>
              <p><strong>Target Audience:</strong> {scheme.targetAudience}</p>
              <p><strong>Monthly Contribution:</strong> {scheme.investmentPlan.monthly_contribution}</p>
              <p><strong>Chit Period:</strong> {scheme.investmentPlan.chit_period} months</p>
              <p><strong>Total Fund Value:</strong></p>
              <ul>
                {scheme.investmentPlan.total_fund_value.map((item, index) => (
                  <li key={index}>{item.duration} months: {item.value}</li>
                ))}
              </ul>
              <p><strong>Benefits:</strong></p>
              <ul>
                {scheme.benefits.map((benefit, index) => (
                  <li key={index}>{benefit}</li>
                ))}
              </ul>
              <p><strong>Number of Slots:</strong> {scheme.number_of_slots}</p>
              <button onClick={() => deleteScheme(scheme._id)} style={{ backgroundColor: 'red', color: 'white' }}>
                Delete Scheme
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default SchemesDisplay;
